INSERT INTO `tb_listpertanyaanekonomi` (`id_pertanyaan`, `pertanyaan`, `ganda_a`, `ganda_b`, `ganda_c`, `ganda_d`, `ganda_e`, `jawaban`) VALUES
(1, '<p>Pembangunan ekonomi adalah&hellip;</p>\r\n', '<p>Segala hal yang berhubungan dengan masalah tenaga kerja dan masalah suatu negara</p>\r\n', '<p>Proses peningkatan kapasitas suatu negara dalam jangka panjang untuk memproduksi berbagai macam produk berupa barang maupun jasa yang diperuntukkan bagi rakyat</p>\r\n', '<p>Usaha untuk menaikkan dan mempertahankan kenaikan pendapatan per kapita dengan tetap memperlihatkan tingkat pertumbuhan penduduk disertai adanya perubahan mendasar dalam struktur ekonomi</p>\r\n', '<p>Usaha untuk meningkatkan jumlah produksi yang ada di suatu negara dalam jangka panjang</p>\r\n', '<p>Usaha untuk menaikkan dan mempertahankan kenaikan pendapatan per kapita dengan tetap memperlihatkan tingkat pertumbuhan penduduk yang tidak disertai adanya perubahan mendasar dalam struktur ekonomi</p>\r\n', 'C'),
(2, '<p>Faktor pembangunan ekonomi dilihat dari faktor ekonomi</p>\r\n', '<p>Sumber daya manusia dan Akumulasi modal</p>\r\n', '<p>Sumber daya alam atau tanah dan akumulasi modal</p>\r\n', '<p>Kemajuan teknologi dan politik</p>\r\n', '<p>Sosial budaya dan Tanah</p>\r\n', '<p>Tanah dan Sumber daya manusia</p>\r\n', 'B'),
(3, '<p>Berikut adalah faktor-faktor yang mempengaruhi pembangunan ekonomi :</p>\r\n\r\n<ol>\r\n	<li>Tanah</li>\r\n	<li>Budaya</li>\r\n	<li>Kemajuan Teknologi</li>\r\n	<li>SDM</li>\r\n	<li>Modal</li>\r\n	<li>Organisasi</li>\r\n	<li>Politik</li>\r\n</ol>\r\n\r\n<p>Yang merupakan faktor non ekonomi dalam mempengaruhi pembangunan ekonomi adalah &hellip;</p>\r\n', '<p>2, 6 dan 7</p>\r\n', '<p>2, 4 dan 7</p>\r\n', '<p>2, 3 dan 4</p>\r\n', '<p>1, 2 dan 7</p>\r\n', '<p>5, 6 dan 7</p>\r\n', 'B'),
(4, '<p>Berikut salah satu indikator yang menentukan keberhasilan pembangunan ekonomi dari indeks kualitas hidup adalah&hellip;</p>\r\n', '<p>Tingginya pendapatan per kapita</p>\r\n', '<p>Tingginya pendapatan nasional</p>\r\n', '<p>Tingginya pertumbuhan penduduk</p>\r\n', '<p>Kesempatan kerja yang rendah</p>\r\n', '<p>Tingkat kematian bayi yang rendah</p>\r\n', 'E'),
(5, '<p>Berikut data permasalahan perekonomian :</p>\r\n\r\n<ol>\r\n	<li>Kemiskinan</li>\r\n	<li>Keterbelakangan</li>\r\n	<li>Pengangguran</li>\r\n	<li>Kesenjangan Penduduk</li>\r\n	<li>Kemakmuran</li>\r\n	<li>Kesejahteraan</li>\r\n</ol>\r\n\r\n<p>Yang termasuk masalah pembangunan ekonomi di negara berkembang ditunjukan dengan nomor &hellip;</p>\r\n', '<p>1 2 dan 3</p>\r\n', '<p>2 3 dan 4</p>\r\n', '<p>3 4 dan 5</p>\r\n', '<p>4 5 dan 6</p>\r\n', '<p>1 5 dan 6</p>\r\n', 'A');

